import React, {Component} from 'react';
import {getCV} from "../api/apiCalls";
import CvListItem from "./CVListItem";


class CvList extends Component {



    state = {
        cvs: []
    };

    componentDidMount() {
        getCV().then(response => {
            this.setState({
                cvs: response.data.content
            });
        });
    }

    render() {
        const {cvs} = this.state;
        return (
            <div className="card">
                <h3 className="card-header text-center">CVler</h3>
                <div className="list-group-flush">
                    {cvs.map(cv => (
                            <CvListItem className="list-group-item list-group-item-action" key={cv.email}>
                                {cv.name}
                            </CvListItem>
                    ))}
                </div>

            </div>
        );
    }
}

export default CvList;
