import React, {useState} from 'react';
import {login} from "../api/apiCalls";
import {connect, useSelector} from "react-redux";
import {loginSuccess} from "../redux/authActions";


const LoginPage = (props) => {

    const [username, setUsername] = useState();
    const [password,setPassword] =useState();
    // state = {
    //     username: null,
    //     password: null,
    //     error: null
    // }

    // const onChange = event => {
    //     const {name, value} = event.target;
    //     // this.setState({
    //     //     [name]:value
    //     // })
    // }

    const onClickLogin =  async event => {
        
        event.preventDefault();
        const {username, password} = this.state;

        const creds = {
            username: username,
            password: password
        };

        const {push} =this.props.history;

        this.setState({
            error:null
        });
        try{
            const response = await login(creds);
            push('/');

            const authState = {
                ...response.data,
                password
            }

            this.props.onLoginSuccess(authState)
        } catch (error){

            // this.setState({
            //     error: error.response.data.message
            // });

        }

    }
    return (
        <div className="container">
            <form action="">
                <h1 className="text-center">Login</h1>
                <div className="form-group">
                    <label htmlFor="">Username</label>
                    <input className="form-control"  onChange={(event) => setUsername(event.target.value)}/>
                </div>
                <div className="form-group">
                    <label htmlFor="">Password</label>
                    <input className="form-control"  type="password" onChange={event => setPassword(event.target.value)} />
                </div>
                <div className="text-center mt-1">
                    <button  className="btn btn-primary" onClick={onClickLogin}>
                        Login
                    </button>
                </div>

            </form>
        </div>
    );

}

const mapDispatchToProps = (dispatch) => {
    return {
        onLoginSuccess: (authState) => {
            return dispatch(loginSuccess(authState))
        }
    }
}

export default connect(null, mapDispatchToProps)(LoginPage);
