import React, { Component } from 'react';

import { connect } from "react-redux";
import { login, logout } from "../redux/authActions";

class LoginPage extends Component {

    componentDidMount() {
        this.props.dispatch(logout());
    }
    state = {
        username: '',
        password: ''
    }
    handleSubmit = e => {
        e.preventDefault();
        const { dispatch } = this.props;
        const { username, password } = this.state;
        dispatch(login(username, password));
    }
    handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    render() {
        const { isAuthenticated, error, errorMessage } = this.props;
        if (isAuthenticated)
            this.props.history.push('/');
        return (
            <div className="container">
                <form action="">
                    <h1 className="text-center">Login</h1>
                    <div className="form-group">
                        <label htmlFor="">Username</label>
                        <input className="form-control" name="username" onChange={this.onChange}/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Password</label>
                        <input className="form-control" name="password" type="password" onChange={this.onChange}/>
                    </div>
                    <div className="text-center">
                        <button  className="btn btn-primary" onClick={this.onClickLogin}>
                            Login
                        </button>
                    </div>

                </form>
            </div>
        );
    }
}

const mapStateToProps = state => {
    const { isAuthenticated, error, errorMessage, user } = state.auth;
    return {
        isAuthenticated,
        error,
        errorMessage,
        user
    }
}

export default connect(mapStateToProps)(LoginPage);






