import React, {Component} from 'react';
import logo from '../assets/cv.png';
import {Link} from 'react-router-dom';
import {connect} from "react-redux";


class TopBar extends Component {

    onClickLogout = () => {
        const action = {
            type: 'logout-success'
        };
        this.props.dispatch(action);
    }

    render() {
        const {username, isLoggedIn} = this.props;

        let links = (
            <ul className="navbar-nav">
                <li>
                    <Link className="nav-link " to="/login">
                        Login
                    </Link>
                </li>
                <li>
                    <Link className="nav-link " to="/signup">
                        Sign Up
                    </Link>
                </li>
            </ul>
        );
        if (isLoggedIn) {
            links = (
                <ul className="navbar-nav">
                    <li>
                        <Link className="nav-link" to={`/user/${username}`}>
                            {username}
                        </Link>

                    </li>
                    <li className="nav-link" onClick={this.onClickLogout} style={{cursor: 'pointer'}}>Logout</li>
                </ul>
            );
        }

        return (
            <div className="shadow-sm mb-2">
                <nav className="navbar navbar-dark bg-dark navbar-expand">
                    <div className="container-fluid">
                        <Link className="navbar-brand nav-item" to="/">
                            <img src={logo} width="60" alt=""/> CV Project
                        </Link>
                        {links}
                    </div>
                </nav>
            </div>

        );
    }
}

const mapStateToProps = (store) => {
    return {
        isLoggedIn: store.isLoggedIn,
        username: store.username
    };
};

export default connect(mapStateToProps)(TopBar);
