import axios from "axios";

export const signup = (body) => {
    return axios.post("/api/authentication/sign-up", body);
};

export const login = creds => {
    return axios.post("/api/authentication/sign-in", creds);
};

export const getCV = () => {
    return axios.get('/api/cv');
}
