import React, {Component} from 'react';
import {getCV} from "../api/apiCalls";
import CvListItem from "./CVListItem";


class CvList extends Component {

    state = {
        page: {
            content: [],
            size:3,
            number:0
        }
    };

    componentDidMount() {

    }

    onClickNext = () => {
        const nextPage = this.state.page.number + 1;
        getCV(nextPage).then(response => {
            this.setState({
                page: response.data
            });
        });
    }

