import React, {Component} from 'react';
import {getCV} from "../api/apiCalls";
import CvListItem from "./CVListItem";


class CvList extends Component {

    state = {
        page: {
            content: [],
            size:1,
            number:0
        }
    };

    componentDidMount() {
        getCV().then(response => {
            this.setState({
                page: response.data
            });
        });
    }

    render() {
        const {content : cvs, last,  first} = this.state.page;
        return (
            <div className="card">
                <h3 className="card-header text-center">CVler</h3>
                <div className="list-group-flush">
                    {cvs.map(cv => (
                            <CvListItem  key={cv.email} cv={cv} />

                    ))}
                </div>
                <div>
                    {last ===false && <button>next</button>}
                </div>

            </div>
        );
    }
}

export default CvList;
