import {createStore} from "redux";
import authReducer from "./authReducer";

const loggedInState = {
    isLoggedIn: true,
    username: 'user1',
    password: 'user1'
};

const configureStore = () => {
    const cvAuth = localStorage.getItem('cv-auth')

    const store = createStore(authReducer,  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());

    store.subscribe(() => {
        localStorage.setItem('cv-auth',JSON.stringify(store.getState()))
    })

    return store;
}

export default configureStore;
