import React from "react";
import axios from "axios";


class UserSignupPage extends React.Component {
    state = {
        name: null,
        username: null,
        password: null,
        pendingApiCall: false
    }
    onChange = event => {
        const {name, value} = event.target;
        // const value = event.target.value;
        // const name = event.target.name;
        this.setState({
            [name]: value
        });
    };

    onClickSignup = event => {
        event.preventDefault(); //browserın bizim yerimize bisey yapmasını engellemiş oluyoruz

        const {name, username, password} = this.state;
        const body = {
            name: name,
            username: username,
            password: password
        };
        axios.post("/api/authentication/sign-up", body)
    }

    // onChangeName = event => {
    //     this.setState({
    //         name: event.target.value
    //     })
    // }
    // onChangeUsername = event => {
    //         this.setState({
    //             username: event.target.value
    //         });
    // };
    //
    // onChangePassword = event => {
    //     this.setState({
    //         password:event.target.value
    //     })
    // }


    render() {
        return (
            <div className="container">
                <form action="">
                    <h1 className="text-center">SignupPage</h1>
                    <div className="form-group">
                        <label htmlFor="">Name</label>
                        <input className="form-control" name="name" onChange={this.onChange}/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Username</label>
                        <input className="form-control" name="username" onChange={this.onChange}/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Password</label>
                        <input className="form-control" name="password" type="password" onChange={this.onChange}/>
                    </div>
                    <div className="text-center">
                        <button className="btn btn-primary" onClick={this.onClickSignup}>Sign Up</button>
                    </div>
                </form>
            </div>
        );
    }
}

export default UserSignupPage;
